const search = document.getElementById("search");
const div = document.getElementById("div");
const close = document.getElementById("close");
search.addEventListener("click", () => {
  div.classList.remove("hidden");
  div.classList.add("flex");
  search.style.display = "none";
});
close.addEventListener("click", () => {
  div.classList.add("hidden");
  div.classList.remove("flex");
  search.style.display = "block";
});
const text = "Dinner with us Friend or With Family Or .........";
let index = 0;
function types() {
  if (index < text.length) {
    document.getElementById("text").textContent += text.charAt(index);
    index++;
    setTimeout(types, 135);
  }
}
types();
const observer = new IntersectionObserver(
  (entries) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        entry.target.classList.add("animate-fade-up-show");
      } else {
        entry.target.classList.remove("animate-fade-up-show"); // اختیاری: وقتی از دید خارج شد حذف کن
      }
    });
  },
  {
    threshold: 0.3,
  }
);

document
  .querySelectorAll(".scroll-animate")
  .forEach((el) => observer.observe(el));
// Search
const serials = [
  { name: "Breaking Bad", genre: "Crime", year: 2008 },
  { name: "Better Call Saul", genre: "Drama", year: 2015 },
  { name: "Game of Thrones", genre: "Fantasy", year: 2011 },
  { name: "Sherlock", genre: "Mystery", year: 2010 },
  { name: "Stranger Things", genre: "Sci-Fi", year: 2016 },
  { name: "Band of Brothers", genre: "War", year: 2001 },
];

const input = document.getElementById("searchInput");
const resultDiv = document.getElementById("result");

input.addEventListener("input", function () {
  const query = input.value.toLowerCase();
  resultDiv.innerHTML = ""; // پاک کردن نتایج قبلی

  const filtered = serials.filter((serial) =>
    serial.name.toLowerCase().includes(query)
  );

  if (filtered.length === 0) {
    resultDiv.innerHTML = "<p>سریالی یافت نشد</p>";
  } else {
    filtered.forEach((serial) => {
      const div = document.createElement("div");
      div.className = "serial";
      div.innerHTML = `<strong>${serial.name}</strong><br>
         ژانر: ${serial.genre} <br>
         سال: ${serial.year}`;
      resultDiv.appendChild(div);
    });
  }
});
